mapper.select:
SELECT m.id AS capabilityId, m.name AS capabilityName, COALESCE(SUM(CASE WHEN milestone_counts.milestone_id IS NOT NULL THEN milestone_counts.milestone_count ELSE 0 END), 0) AS totalMilestoneCount, (SELECT COUNT(DISTINCT s.snum) FROM _sw_student s WHERE 1=1
AND s.major_1 = #{major}
AND s.semester_count = #{term}
AND SUBSTRING(s.snum, 2, 2) = #{entryYear}
) AS groupSize FROM _sw_milestone m LEFT JOIN ( SELECT m.id AS milestone_id, COUNT(DISTINCT r.subitem_id) AS milestone_count FROM _sw_milestone m JOIN _sw_category_milestone cm ON m.id = cm.milestone_id JOIN _sw_mileage_category c ON cm.category_id = c.id JOIN _sw_mileage_subitem si ON c.id = si.category_id JOIN _sw_mileage_record r ON si.id = r.subitem_id JOIN _sw_student s ON r.snum = s.snum WHERE 1=1
AND s.major_1 = #{major}
AND s.semester_count = #{term}
AND SUBSTRING(s.snum, 2, 2) = #{entryYear}
GROUP BY m.id ) milestone_counts ON m.id = milestone_counts.milestone_id GROUP BY m.id, m.name ORDER BY m.id;

SELECT
    r.semester,
    (SELECT COUNT(DISTINCT r2.subitem_id)
    FROM _sw_mileage_record r2
    WHERE r2.snum = #{studentId}
        AND r2.semester <= r.semester
    ) AS userMilestoneCount
FROM _sw_mileage_record r
WHERE r.snum = #{studentId}
GROUP BY r.semester
ORDER BY r.semester;
