mapper.update:
UPDATE _sw_student
SET is_apply = 1,
    apply_date = #{applyDate},
    isChecked = #{isChecked}
WHERE snum = #{studentId}
    AND is_apply != 1

mapper.insert:
INSERT INTO _sw_mileage_scholarship (snum, is_apply, semester)
SELECT
    snum,
    #{isChecked},
    #{semester}
FROM _sw_student
WHERE snum = #{studentId}
  AND NOT EXISTS (
    SELECT 1
    FROM _sw_mileage_scholarship
    WHERE snum = #{studentId}
      AND semester = #{semester}
);
